import React,{useState,useEffect} from "react";
import "./App.css";
import youtube from "./apis/youtubeapi";
import VideoList from "./components/VideoList";
import VideoDetails from "./components/VideoDetails";
import Search from "./components/Search"


const App = ({currentUser}) => {

  const [videos,setVideos] = useState([]);
  const [selectedVideos,setSelectedVideos] = useState("");
  const [commentList,setCommentList] = useState({})

  const onSelectVideo = (video) => {
    setSelectedVideos(video);
  };

  useEffect(() => {
    onTermSubmit("Cricket India");
  }, [])

  const onTermSubmit = async (term) => {
    const res = await youtube.get("/search", {
      params: {
        q: term,
      },
    });

    setVideos(res.data.items);
      //setSelectedVideos(res.data.items[0]);
      res.data.items.forEach((item)=>{
      const {id} = item;
      commentList[id.videoId] = [];
    })

  };
  const setComments = (videoId,comments)=>{
   const tempArr = commentList[videoId]
   commentList[videoId] = [...tempArr,...comments];
  }
    return (
      <>

        <div className="container">
          <div className="row yt">
            <h1 className="text-center col-md-12">
              &nbsp; Blindside video task
            </h1>
          </div>
          <div className="row my-2">
            <div className="col-md-8">
              <Search onFormSubmit={onTermSubmit} />
              <VideoDetails currentUser={currentUser} video={selectedVideos} commentList={commentList} setComments={setComments}/>
            </div>
            <div className="col-md-4">
              <VideoList
                videos={videos}
                onSelectVideo={onSelectVideo}
              />
            </div>
          </div>
        </div>
      </>
    );

}

export default App;
