import "../App.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState , useEffect} from 'react';
const VideoDetails = ({ video , currentUser, commentList, setComments}) => {
  
  const[comment,setComment] = useState("");
  const[display,setDisplay] = useState([]);
  const[commentView,setCommentView] = useState(false);

  
   
  const clickHandler= ()=>{
    setDisplay(commentList[video.id.videoId])
  }
  if (!video) {
    return <div className ="fa fa-spinner">  Loading...</div>;
  }
const videoSrc = `https://www.youtube.com/embed/${video.id.videoId}`;

const handleSubmit =(e)=>{
e.preventDefault();
const arr = [...display,`${currentUser} : ${comment}`]
setDisplay(arr);
console.log(arr);
setComments(video.id.videoId,display);

}
const handle =(e)=>{
  e.preventDefault();
  setComment(e.target.value);
  
  }
return (
    <>
      <div>
        <iframe
          src={videoSrc}
          title="video Player"
          className="w-100"
          style={{ height: "380px" }}
        ></iframe>
      </div>
      <div className="border p-2">
        <h4>{video.snippet.title}</h4>
        <p>{video.snippet.description}</p>
        
         <form onSubmit= {handleSubmit}>
         <p className="font-weight-bold">Comments</p>
        <div className="input-group">
       
        <input type="text" className="form-control" 
        placeholder="Type your comment" aria-label="  Type your comment" 
        aria-describedby="input-group-right" value ={comment} onChange ={handle} ></input>
       </div>
       {
         display.map((item,index)=>{
           return <p key={index}>{item}</p>
         })
       }
       <h1 onClick={clickHandler,setCommentView(!commentView)}>Comments</h1>
       {
       commentView 
       ?<p>{commentList[video.id.videoId]}</p>
       :<p>Comments</p>
        }
       </form>
        </div>
         
      
    </>
  );
};

export default VideoDetails;
